import React, { useState } from 'react';


const ImageSlider = () => {
  const images = ["/images/ayat1.jpg", "/images/ayat2.jpg", "/images/ayat3.jpg"];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div>
      <div className='relative h-96 w-full rounded-xl overflow-hidden'>
        <img src={images[currentImageIndex]} alt={`Image ${currentImageIndex + 1}`} className='h-full w-full object-cover' />
        <button onClick={handlePrev} className='absolute top-1/2 left-4 transform -translate-y-1/2 focus:outline-none'>
          Previous
        </button>
        <button onClick={handleNext} className='absolute top-1/2 right-4 transform -translate-y-1/2 focus:outline-none'>
          Next
        </button>
      </div>
    </div>
  );
};

export default ImageSlider;
